import threading
from http.server import SimpleHTTPRequestHandler, HTTPServer
import datetime
import socket
import tkinter as tk
import sys
import os
import webbrowser
import base64  # Добавляем модуль base64

# Создаем папку LOG, если ее нет
if not os.path.exists("LOG"):
    os.makedirs("LOG")

class MyHandler(SimpleHTTPRequestHandler):
    def do_GET(self):
        # Просто игнорируем GET запросы
        pass

    def do_POST(self):
        content_length = int(self.headers['Content-Length'])
        post_data = self.rfile.read(content_length)
        print("Received POST data: ", post_data.decode('utf-8'))

        # Логирование в файл
        with open("LOG/log.txt", "a") as log_file:
            log_file.write(f"[{datetime.datetime.now().strftime('%Y-%m-%d %H:%M:%S')}]{post_data.decode('utf-8')}\n")

        # Отображение лога в GUI
        log_to_window(f"[{datetime.datetime.now().strftime('%Y-%m-%d %H:%M:%S')}]{post_data.decode('utf-8')}")

        self.send_response(200)
        self.end_headers()
        self.wfile.write(b'Data received!')

    # Переопределение метода log_message для подавления лишнего логирования
    def log_message(self, format, *args):
        pass

def run_server():
    global httpd
    server_address = ('', 8000)
    httpd = HTTPServer(server_address, MyHandler)
    print('Starting server on', socket.gethostbyname(socket.gethostname()) + ':8000')
    httpd.serve_forever()

def stop_server():
    global httpd
    if httpd:
        httpd.shutdown()
        httpd.server_close()

def on_closing():
    stop_server()
    root.destroy()
    sys.exit()

def update_info_label(label):
    label.config(text='Server running on: ' + socket.gethostbyname(socket.gethostname()) + ':8000')

def update_log(log_text, message):
    log_text.config(state=tk.NORMAL)
    log_text.insert(tk.END, message + '\n')  # Добавляем символ новой строки
    log_text.config(state=tk.DISABLED)
    log_text.see(tk.END)  # Прокручиваем к последней строке

def log_to_window(message):
    root.after(0, update_log, log_window, message)

def copy_to_clipboard(event):
    # Функция копирования текста в буфер обмена
    widget = event.widget
    text = widget.get("sel.first", "sel.last")
    root.clipboard_clear()
    root.clipboard_append(text)

def open_email(event):
    # Функция открытия почтового клиента
    webbrowser.open_new("mailto:info@spirkin.uz")

def create_icon_photo(base64_string):
    icon_data = base64.b64decode(base64_string)
    icon_photo = tk.PhotoImage(data=icon_data)
    return icon_photo

# Ваше изображение в формате base64
icon_base64 = """
    
"""

if __name__ == '__main__':
    root = tk.Tk()
    root.title('Server Status')
    root.iconphoto(True, create_icon_photo(icon_base64))

    root.protocol("WM_DELETE_WINDOW", on_closing)

    info_label = tk.Label(root, text='Server not started yet', font=('Arial', 12))
    info_label.grid(row=0, column=0, columnspan=2, pady=10)

    log_window = tk.Text(root, wrap="none", height=10, state=tk.DISABLED)
    log_window.grid(row=1, column=0, padx=10, pady=10, sticky="nsew")
    root.grid_rowconfigure(1, weight=1)  # Растягиваемая строка
    root.grid_columnconfigure(0, weight=1)  # Растягиваемый столбец

    # Добавление вертикального скроллбара
    scrollbar_vertical = tk.Scrollbar(root, orient="vertical", command=log_window.yview)
    scrollbar_vertical.grid(row=1, column=1, sticky="ns")
    log_window.config(yscrollcommand=scrollbar_vertical.set)

    # Добавление горизонтального скроллбара
    scrollbar_horizontal = tk.Scrollbar(root, orient="horizontal", command=log_window.xview)
    scrollbar_horizontal.grid(row=2, column=0, sticky="ew")
    log_window.config(xscrollcommand=scrollbar_horizontal.set)

    # Привязываем копирование к правому клику мыши
    log_window.bind("<Button-3>", copy_to_clipboard)

    # Добавляем адрес электронной почты
    email_label = tk.Label(root, text='info@spirkin.uz', font=('Arial', 10), fg='blue', cursor='hand2')
    email_label.grid(row=2, column=0, pady=10, sticky="nsew")
    email_label.bind("<Button-1>", open_email)

    server_thread = threading.Thread(target=run_server)
    server_thread.start()

    update_info_label(info_label)

    root.mainloop()
